INSERT INTO `autoresponders` (`type`, `name`, `subject`, `contents`, `helpid`, `description`) VALUES
(8, 'VPS Welcome Email', 'VPS Hosting', '<p>Dear [CLIENTNAME],</p><p>Thank you for choosing us as your VPS hosting provider. Your server has been activated and is now fully functional.&nbsp;</p><p>We recommend changing the root password of your server after your first login for improved security.</p><p><b>Your Server Details:</b></p><p>Hostname: [CUSTOMPACKAGE_VM Hostname]<br><span style=\"background-color: initial;\">Primary IP: [DOMAINIP]<br></span><span style=\"background-color: initial;\">Root Password: [CUSTOMPACKAGE_VM Password]&nbsp;<br></span><span style=\"background-color: initial;\">Server Location: [CUSTOMPACKAGE_VM Location]&nbsp;<br></span><span style=\"background-color: initial;\">Operating System: [CUSTOMPACKAGE_VM Operating System]&nbsp;</span></p><p><b>SSH Access Details:</b></p><p>Hostname: [CUSTOMPACKAGE_VM Hostname]<br><span style=\"background-color: initial;\">Primary IP: [DOMAINIP]<br></span><span style=\"background-color: initial;\">Root Password: [CUSTOMPACKAGE_VM Password]</span></p><p><span style=\"background-color: initial;\">Need Any Assistance?<br></span><span style=\"background-color: initial;\">If you have any issues accessing your new server feel free to contact us at [SUPPORTEMAIL] or [CLIENTAPPLICATIONURL]</span></p>', 1, 'Sample VPS Welcome Email');