UPDATE autoresponders SET contents="Due Date: [DATE]\r\nInvoice #[INVOICENUMBER]\r\n\r\nATTN: [CLIENTNAME],\r\n\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  \r\n\r\nInvoice Description:\r\n[INVOICEDESCRIPTION]\r\n\r\nTotal Due: [AMOUNT]\r\nPaid: [PAID]\r\nBalance Due: [BALANCEDUE]\r\n\r\nPayment Instructions:\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password\r\n2) Click on the \"Billing\" tab\r\n3) Select the invoice you want to pay\r\n4) Click on pay invoice\r\n\r\nWe greatly appreciate your efforts to ensure that payment\r\nis received in a timely manner.\r\n\r\nIf you have any questions regarding your account or if \r\nyou wish to pay by check, please feel free to contact us.\r\n\r\nNote: This email will serve as an official invoice for this payment.\r\n\r\n[COMPANYNAME]\r\n[BILLINGEMAIL]" WHERE name='Invoice Template' AND contents="Due Date: [DATE]\r\nInvoice #[INVOICENUMBER]\r\n\r\nATTN: [CLIENTNAME],\r\n\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  \r\n\r\nInvoice Description:\r\n[INVOICEDESCRIPTION]\r\n\r\nTotal Due: [AMOUNT]\r\nPaid: [PAID]\r\nBalance Due: [BALANCEDUE]\r\n\r\nPayment Instructions:\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password\r\n2) Click on the \"Billing\" tab\r\n3) Select the invoice you want to pay\r\n4) Click on pay invoice\r\n\r\nWe greatly appreciate your efforts to ensure that payment\r\nis received in a timely matter.\r\n\r\nIf you have any questions regarding your account or if \r\nyou wish to pay by check, please feel free to contact us.\r\n\r\nNote: This email will serve as an official invoice for this payment.\r\n\r\n[COMPANYNAME]\r\n[BILLINGEMAIL]";
UPDATE autoresponders SET contents_html="<HTML><head></head><body>Due Date: [DATE]<br />\r\nInvoice #[INVOICENUMBER]<br />\r\n<br />\r\nATTN: [CLIENTNAME],<br />\r\n<br />\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  <br />\r\n<br />\r\nInvoice Description:<br />\r\n[INVOICEDESCRIPTION]<br />\r\n<br />\r\nTotal Due: [AMOUNT]<br />\r\nPaid: [PAID]<br />\r\nBalance Due: [BALANCEDUE]<br />\r\n<br />\r\nPayment Instructions:<br />\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password<br />\r\n2) Click on the \"Billing\" tab<br />\r\n3) Select the invoice you want to pay<br />\r\n4) Click on pay invoice<br />\r\n<br />\r\nWe greatly appreciate your efforts to ensure that payment<br />\r\nis received in a timely manner.<br />\r\n<br />\r\nIf you have any questions regarding your account or if <br />\r\nyou wish to pay by check, please feel free to contact us.<br />\r\n<br />\r\nNote: This email will serve as an official invoice for this payment.<br />\r\n<br />\r\n[COMPANYNAME]<br />\r\n[BILLINGEMAIL]</body></HTML>" WHERE name='Invoice Template' AND contents_html="<HTML><head></head><body>Due Date: [DATE]<br />\r\nInvoice #[INVOICENUMBER]<br />\r\n<br />\r\nATTN: [CLIENTNAME],<br />\r\n<br />\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  <br />\r\n<br />\r\nInvoice Description:<br />\r\n[INVOICEDESCRIPTION]<br />\r\n<br />\r\nTotal Due: [AMOUNT]<br />\r\nPaid: [PAID]<br />\r\nBalance Due: [BALANCEDUE]<br />\r\n<br />\r\nPayment Instructions:<br />\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password<br />\r\n2) Click on the \"Billing\" tab<br />\r\n3) Select the invoice you want to pay<br />\r\n4) Click on pay invoice<br />\r\n<br />\r\nWe greatly appreciate your efforts to ensure that payment<br />\r\nis received in a timely matter.<br />\r\n<br />\r\nIf you have any questions regarding your account or if <br />\r\nyou wish to pay by check, please feel free to contact us.<br />\r\n<br />\r\nNote: This email will serve as an official invoice for this payment.<br />\r\n<br />\r\n[COMPANYNAME]<br />\r\n[BILLINGEMAIL]</body></HTML>";

UPDATE autoresponders SET contents="Dear [CLIENTNAME],\r\n\r\nPayment was rejected for Invoice #[INVOICENUMBER], sent on [DATE].\r\n\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  \r\n\r\nInvoice Description:\r\n[INVOICEDESCRIPTION]\r\n\r\nTotal Due: [AMOUNT]\r\nPaid: [PAID]\r\nBalance Due: [BALANCEDUE]\r\n\r\nPayment Instructions:\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password\r\n2) Click on the \"Billing\" tab\r\n3) Select the invoice you want to pay\r\n4) Click on pay invoice\r\n\r\nWe greatly appreciate your efforts to ensure that payment\r\nis received in a timely manner.\r\n\r\nIf you have any questions regarding your account or if \r\nyou wish to pay by check, please feel free to contact us.\r\n\r\nNote: This email will serve as an official invoice for this payment.\r\n\r\n[COMPANYNAME]\r\n[BILLINGEMAIL]" WHERE name='Payment Rejected Template' AND contents="Dear [CLIENTNAME],\r\n\r\nPayment was rejected for Invoice #[INVOICENUMBER], sent on [DATE].\r\n\r\nYou currently have a balance of [BALANCEDUE] due on your account with [COMPANYNAME].  \r\n\r\nInvoice Description:\r\n[INVOICEDESCRIPTION]\r\n\r\nTotal Due: [AMOUNT]\r\nPaid: [PAID]\r\nBalance Due: [BALANCEDUE]\r\n\r\nPayment Instructions:\r\n1) Log into our client management site at [CLIENTAPPLICATIONURL] with your username and password\r\n2) Click on the \"Billing\" tab\r\n3) Select the invoice you want to pay\r\n4) Click on pay invoice\r\n\r\nWe greatly appreciate your efforts to ensure that payment\r\nis received in a timely matter.\r\n\r\nIf you have any questions regarding your account or if \r\nyou wish to pay by check, please feel free to contact us.\r\n\r\nNote: This email will serve as an official invoice for this payment.\r\n\r\n[COMPANYNAME]\r\n[BILLINGEMAIL]";
