INSERT INTO `registrars` VALUES ('resellerclub', 'us', 'a:2:{s:7:"purpose";a:3:{s:2:"ID";i:1;s:11:"description";s:13:"Nexus Purpose";s:7:"options";a:5:{s:10:"For Profit";a:2:{s:11:"description";s:24:"Business use for profit.";s:5:"value";s:2:"P1";}s:10:"Non-profit";a:2:{s:11:"description";s:68:"Non-profit business, club, association, religious organization, etc.";s:5:"value";s:2:"P2";}s:8:"Personal";a:2:{s:11:"description";s:13:"Personal use.";s:5:"value";s:2:"P3";}s:11:"Educational";a:2:{s:11:"description";s:19:"Education purposes.";s:5:"value";s:2:"P4";}s:10:"Government";a:2:{s:11:"description";s:20:"Government purposes.";s:5:"value";s:2:"P5";}}}s:8:"category";a:3:{s:2:"ID";i:2;s:11:"description";s:14:"Nexus Category";s:7:"options";a:5:{s:10:"US Citizen";a:2:{s:11:"description";s:48:"A natural person who is a United States citizen.";s:5:"value";s:3:"C11";}s:18:"Permanent Resident";a:2:{s:11:"description";s:119:"A natural person who is a permanent resident of the United States of America, or any of its possessions or territories.";s:5:"value";s:3:"C12";}s:15:"Business Entity";a:2:{s:11:"description";s:454:"A US-based organization or company (A US-based organization or company formed within one of the fifty (50) U.S. states, the District of Columbia, or any of the United States possessions or territories, or organized or otherwise constituted under the laws of a state of the United States of America, the District of Columbia or any of its possessions or territories or a U.S. federal, state, or local government entity or a political subdivision thereof).";s:5:"value";s:3:"C21";}s:14:"Foreign Entity";a:2:{s:11:"description";s:348:"A foreign entity or organization (A foreign entity or organization that has a bona fide presence in the United States of America or any of its possessions or territories who regularly engages in lawful activities (sales of goods or services or other business, commercial or non-commercial, including not-for-profit relations in the United States)).";s:5:"value";s:3:"C31";}s:15:"US Based Office";a:2:{s:11:"description";s:60:"Entity has an office or other facility in the United States.";s:5:"value";s:3:"C32";}}}}');
