# ---------------------------------------------------------
# THIS TABLE WILL BE USED FOR EMAIL TEMPLATES OF DIFFERENT TYPES
# ---------------------------------------------------------
ALTER TABLE `autoresponders` ADD `type` TINYINT( 4 ) NOT NULL DEFAULT '1' AFTER `id`;
ALTER TABLE `autoresponders` ADD `helpid` INT( 3 ) NOT NULL DEFAULT '0';
ALTER TABLE `autoresponders` ADD `description` TEXT;
ALTER TABLE `autoresponders` CHANGE `name` `name` VARCHAR( 75 ) NOT NULL;

# Add all the billing email setting to autoresponder
