# -----------------------------------------------------------
# Team Status Autoresponder Email Templates
# -----------------------------------------------------------
INSERT autoresponders (`type`,`name`,subject,contents,contents_html,description,helpid) VALUES( 6,'Team Status Reply Template','Team Status Reply',"[TEAMSTATUS]\r\n\r\nIn reply to [REPLIEDTEAMSTATUSUSERNAME] ([REPLIEDTEAMSTATUSDATE]):\r\n\r\n> [REPLIEDTEAMSTATUS]","<HTML><head></head><body>[TEAMSTATUS]<br />\r\n<br />\r\nIn reply to [REPLIEDTEAMSTATUSUSERNAME] ([REPLIEDTEAMSTATUSDATE]):<br />\r\n<br />\r\n> [REPLIEDTEAMSTATUS]</body></HTML>",'E-mail send to a user when someone replies to one of his Team Status.',56);
INSERT autoresponders (`type`,`name`,subject,contents,contents_html,description,helpid) VALUES( 6,'Team Status Activity Template','Your Team Status activity',"Here are the latest updates:\r\n\r\n\r\n[TEAMSTATUSDYNAMICBLOCK]","<HTML><head></head><body>Here are the latest updates:<br />\r\n<br />\r\n<br />\r\n[TEAMSTATUSDYNAMICBLOCK]</body></HTML>",'E-mail send to the Team Status users, to notify them the activity on Team Status.',57);
INSERT autoresponders (`type`,`name`,subject,contents,contents_html,description,helpid) VALUES( 6,'Team Status Activity Dynamic Block Template','na',"[TEAMSTATUSUSERNAME][TEAMSTATUSREPLYINFO]: [TEAMSTATUS]\r\n[TEAMSTATUSDATE]\r\n\r\n","[TEAMSTATUSUSERNAME][TEAMSTATUSREPLYINFO]: [TEAMSTATUS]<br />\r\n[TEAMSTATUSDATE]<br />\r\n<br />\r\n",'Dynamic block used to show the Team Status.',58);
INSERT autoresponders (`type`,`name`,subject,contents,contents_html,description,helpid) VALUES( 6,'Team Status Activity Reply Template','na'," in reply to [REPLIEDTEAMSTATUSUSERNAME]"," in reply to [REPLIEDTEAMSTATUSUSERNAME]",'Text to add when a Team Status is a  reply of another Team Status.',59);
